header-scroll-perf.code:
// Passive event listeners improve scrolling performance
window.addEventListener('scroll', onScroll, { passive: true });

// Use requestAnimationFrame for visual updates
let ticking = false;
function onScroll() {
  if (!ticking) {
    window.requestAnimationFrame(() => {
      updateHeader();
      ticking = false;
    });
    ticking = true;
  }
}

search-debounce.code:
import { debounce } from 'lodash-es'

// 검색 입력 핸들러에 디바운스 적용
const handleSearch = debounce((query) => {
  performSearch(query)
}, 300)

// 입력 이벤트 바인딩
<input @input="handleSearch" />

search-shortcut.code:
// '/' 키로 검색창 열기
const handleKeydown = (event) => {
  if (event.key === '/') {
    // input이나 textarea에 포커스가 있으면 무시
    if (
      event.target.tagName === 'INPUT' ||
      event.target.tagName === 'TEXTAREA' ||
      event.target.isContentEditable
    ) {
      return
    }

    event.preventDefault()
    openSearch()
  }
}

window.addEventListener('keydown', handleKeydown)

// 메뉴에 단축키 힌트 표시
<ShortcutHint :keys="'/'" :active="activeSection === 'work'" />

